    private static List<JSONObject> parseJsonArray(String wayStr) {
        List<JSONObject> wayArr = JSONArray.parseArray(wayStr, JSONObject.class);
        return wayArr;
    }


    public  static void WriteIntoFile(String filePath , String content) throws IOException {
        File file = new File(filePath);
        if(file.exists()){
            file.delete();
        }
        if(!file.getParentFile().exists()){
            file.getParentFile().mkdirs();
        }
        file.createNewFile();
        BufferedWriter out = new BufferedWriter(new FileWriter(file));
        out.write(content); // \r\n即为换行
        out.flush(); // 把缓存区内容压入文件
        out.close();
    }

